export function getFormattedOrderTotalText(orderTotal) {
  if (!orderTotal) {
    return '$ -';
  }

  const USDollar = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  });
  return USDollar.format(orderTotal);
}
export function getCardDeliveryText(order) {
  return `${order.customerStreetAddress1}${order.customerStreetAddress2 ? ', ' + order.customerStreetAddress2 : ''}, ${order.customerCity}, ${order.customerZipCode}, ${order.customerCountry}`;
}
